# -*- coding: utf8 -*-
import logging
from Crypto.Cipher import AES
from Crypto.Cipher import XOR
from Crypto.Hash import SHA
from zlib import crc32

class AES_IGE:
    def __init__(self, key, iv):
        self.key = key
        self.iv = iv
        assert(len(iv) == 32)
        self.aes = AES.new(key, AES.MODE_ECB)
    
    def encrypt(self, data):
        if len(data) % 16 != 0:
            raise ValueError("Input strings must be a multiple of 16 in length")
        result = b''
        x = self.iv[0:16]
        y = self.iv[16:32]
        for i in range((len(data)-1)//16 + 1):
            input_block = data[i*16:(i+1)*16]
            output_block = XOR.new(x).encrypt(input_block)
            output_block = self.aes.encrypt(output_block)
            output_block = XOR.new(y).encrypt(output_block)
            result += output_block
            x = output_block
            y = input_block
        return result

    def decrypt(self, data):
        if len(data) % 16 != 0:
            raise ValueError("Input strings must be a multiple of 16 in length")
        result = b''
        x = self.iv[16:32]
        y = self.iv[0:16]
        for i in range((len(data)-1)//16 + 1):
            input_block = data[i*16:(i+1)*16]
            output_block = XOR.new(x).decrypt(input_block)
            output_block = self.aes.decrypt(output_block)
            output_block = XOR.new(y).decrypt(output_block)
            result += output_block
            x = output_block
            y = input_block
        return result
    
def SHA1(data):
    sha = SHA.new()
    sha.update(data)
    return sha.digest()

def CRC32(data):
    return crc32(data) & 0xffffffff

